import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { HttpModule } from '@angular/http';
import { CircularGaugeAllModule } from '@syncfusion/ej2-angular-circulargauge';
import { GridModule } from '@syncfusion/ej2-angular-grids';
import { ButtonModule, CheckBoxModule } from '@syncfusion/ej2-angular-buttons';
import { Component, ViewEncapsulation } from '@angular/core';
import { ILoadedEventArgs, IAxisLabelRenderEventArgs, GaugeTheme } from '@syncfusion/ej2-angular-circulargauge';

@Component({
    standalone: true,
    imports: [ CircularGaugeAllModule, GridModule, ButtonModule, CheckBoxModule, ],
    selector: 'app-root',
    templateUrl: 'app.component.html',
    encapsulation: ViewEncapsulation.None
})

export class AppComponent {

    public textValues: string[] = ['0', '2', '5', '10', '20', '50', '100', '150', '200'];
    public needleStartWidth: number = 10;
    public needleEndWidth: number = 5;
    public pointerValue: number = 6.2;

    public majorTicks: Object = {
        width: 0,
        interval: 1
    };

    public minorTicks: Object = {
        width: 0
    };

    public lineStyle: Object = {
        width: 0
    };

    public rangeLinearGradient: Object = {
        startValue: '0%',
        endValue: '100%',
        colorStop: [
            { color: '#9e40dc', offset: '0%', opacity: 1 },
            { color: '#d93c95', offset: '70%', opacity: 1 },
        ]
    };

    public labelStyle: Object = {
        font: {
            fontFamily: 'inherit',
        },
        offset: 10
    };

    public animation: Object = {
        enable: false
    };

    public cap: Object = {
        radius: 0,
        border: { width: 0 }
    };

    public tail: Object = {
        length: '0%',
    };

    public pointerLinearGradient: Object = {
        startValue: '0%',
        endValue: '100%',
        colorStop: [
            { color: '#9e40dc', offset: '0%', opacity: 0.2 },
            { color: '#9e40dc', offset: '70%', opacity: 0.5 }]
    };

    public load(args: ILoadedEventArgs): void {
        /* custom code start */
        let selectedTheme: string = location.hash.split('/')[1];
        selectedTheme = selectedTheme ? selectedTheme : 'Material';
        args.gauge.theme = <GaugeTheme>(selectedTheme.charAt(0).toUpperCase() +
            selectedTheme.slice(1)).replace(/-dark/i, 'Dark').replace(/contrast/i, 'Contrast');
        /* custom code end */
    }

    public axisLabelRender(args: IAxisLabelRenderEventArgs): void {
        args.text = this.textValues[(args.value)];
    }

    constructor() {
        // code
    };
}
