import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { HttpModule } from '@angular/http';
import { PivotViewAllModule } from '@syncfusion/ej2-angular-pivotview';
import { PivotFieldListAllModule } from '@syncfusion/ej2-angular-pivotview';
import { ToolbarModule } from '@syncfusion/ej2-angular-navigations';
import { DropDownListAllModule, MultiSelectAllModule } from '@syncfusion/ej2-angular-dropdowns';
import { MaskedTextBoxAllModule, NumericTextBoxAllModule } from '@syncfusion/ej2-angular-inputs';
import { CheckBoxAllModule, RadioButtonAllModule, ButtonAllModule } from '@syncfusion/ej2-angular-buttons';
import { Component, ViewEncapsulation, OnInit, ViewChild } from '@angular/core';
import { IDataOptions, PivotView, IDataSet, VirtualScrollService } from '@syncfusion/ej2-angular-pivotview';
import { GridSettings } from '@syncfusion/ej2-pivotview/src/pivotview/model/gridsettings';
import { Browser, enableRipple } from '@syncfusion/ej2-base';
import { Button } from '@syncfusion/ej2-buttons';
enableRipple(false);

@Component({
    standalone: true,
    imports: [ CommonModule, HttpModule, ToolbarModule, PivotViewAllModule, PivotFieldListAllModule,  NumericTextBoxAllModule, ButtonAllModule, CheckBoxAllModule, RadioButtonAllModule, DropDownListAllModule, MultiSelectAllModule, MaskedTextBoxAllModule],
    selector: 'app-root',
    templateUrl: 'app.component.html',
    encapsulation: ViewEncapsulation.None,
    styleUrls: ['app.component.css'],
    providers: [VirtualScrollService]
})
export class AppComponent {
    public gridSettings: GridSettings;
    public dataSourceSettings: IDataOptions;
    public customername: string[] = ['TOM', 'Hawk', 'Jon', 'Chandler', 'Monica', 'Rachel', 'Phoebe', 'Gunther',
        'Ross', 'Geller', 'Joey', 'Bing', 'Tribbiani', 'Janice', 'Bong', 'Perk', 'Green', 'Ken', 'Adams'];
    public city: string[] = ['New York', 'Los Angeles', 'Chicago', 'Houston', 'Philadelphia', 'Phoenix', 'San Antonio', 'Austin',
        'San Francisco', 'Columbus', 'Washington', 'Portland', 'Oklahoma', 'Las Vegas', 'Virginia', 'St. Louis', 'Birmingham'];
    public date1: number;
    public date2: number;
    public isInit: boolean;
    @ViewChild('pivotview')
    public pivotObj: PivotView;
    @ViewChild('loadbtn')
    public applyBtn: Button;
    public width: string | number;

    data(count: number): Object[] {
        let result: Object[] = [];
        let dt: number = 0;
        for (let i: number = 1; i < (count + 1); i++) {
            dt++;
            let round: string;
            let toString: string = i.toString();
            if (toString.length === 1) {
                round = '0000' + (i);
            }
            else if (toString.length === 2) {
                round = '000' + i;
            }
            else if (toString.length === 3) {
                round = '00' + i;
            } else if (toString.length === 4) {
                round = '0' + i;
            } else {
                round = toString;
            }
            result.push({
                ProductID: 'PRO-' + round,
                City: this.city[Math.round(Math.random() * this.city.length)] || this.city[0],
                Year: "FY " + (dt + 2013),
                CustomerName: this.customername[Math.round(Math.random() * this.customername.length)] || this.customername[0],
                Price: Math.round(Math.random() * 5000) + 5000,
                Sold: Math.round(Math.random() * 80) + 10,
            });
            if (dt / 4 == 1) {
                dt = 0;
            }
        }
        return result;
    };

    show(): void {
        document.getElementById('popup').style.display = 'inline-block';
    }

    applyData(e: Event): void {
        this.show();
        this.isInit = true;
        this.pivotObj.dataSourceSettings.dataSource = this.data(100000) as IDataSet[];
        this.date1 = new Date().getTime();
    }

    ondataBound(args: any): void {
	 if((this.pivotObj.dataSourceSettings.dataSource as IDataSet[]).length > 0) {
        if (this.date1 && this.isInit) {
            this.date2 = new Date().getTime();
            document.getElementById('performanceTime').innerHTML = 'Time Taken: ' + (this.date2 - this.date1) / 1000 + ' sec';
        }
        this.isInit = false;
        this.applyBtn.disabled = true;
        document.getElementById('popup').style.display = 'none';
	  }
      if (Browser.isDevice && this.pivotObj && this.pivotObj.enableRtl) {
        document.querySelector('.control-section').classList.add('e-rtl');
       }
    }

    ngOnInit(): void {
        this.gridSettings = {
            columnWidth: 140
        } as GridSettings;

        this.dataSourceSettings = {
            dataSource: [],
            enableSorting: false,
            expandAll: true,
            formatSettings: [{ name: 'Price', format: 'C0' }],
            rows: [{ name: 'ProductID' }],
            columns: [{ name: 'Year' }],
            values: [{ name: 'Price', caption: 'Unit Price' }, { name: 'Sold', caption: 'Unit Sold' }]
        };

        this.width = Browser.isDevice ? '100%' : 860;
    }
}
