import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { HttpModule } from '@angular/http';
import { ColorPickerModule } from '@syncfusion/ej2-angular-inputs';
import { CheckBoxModule, ButtonModule } from '@syncfusion/ej2-angular-buttons';
import { DropDownListModule } from '@syncfusion/ej2-angular-dropdowns';
import { DialogModule } from '@syncfusion/ej2-angular-popups';
import { UploaderModule, SignatureModule, TextBoxModule, NumericTextBoxModule } from '@syncfusion/ej2-angular-inputs';
import { TabModule, ToolbarModule } from '@syncfusion/ej2-angular-navigations';
import { SplitButtonModule } from '@syncfusion/ej2-angular-splitbuttons';
import { Component, ViewEncapsulation, ViewChild } from '@angular/core';
import { Signature} from '@syncfusion/ej2-inputs';
import { getComponent} from '@syncfusion/ej2-base';
import { SignatureComponent } from '@syncfusion/ej2-angular-inputs';
import { ButtonComponent } from '@syncfusion/ej2-angular-buttons';

/**
 * Default sample
 */

@Component({
    standalone: true,
    imports: [  ColorPickerModule,  CheckBoxModule, DropDownListModule, TabModule, ButtonModule, SplitButtonModule, ToolbarModule, UploaderModule, SignatureModule, TextBoxModule, DialogModule, ButtonModule, NumericTextBoxModule],
    selector: 'app-root',
    templateUrl: 'app.component.html',
    styleUrls: ['app.component.css'],
    encapsulation: ViewEncapsulation.None

})
export class AppComponent {
    @ViewChild('signature') signature: SignatureComponent;
    @ViewChild('signsave') saveBtn: ButtonComponent;
    @ViewChild('signclear') clearBtn: ButtonComponent;

    public saveBtnClick(): void {
        this.signature.save();
    }
    public clearBtnClick(): void {
        this.signature.clear();
        if (this.signature.isEmpty()) {
            this.saveBtn.disabled = true;
            this.clearBtn.disabled = true;
        }
    }
    public change(): void {
        if (!this.signature.isEmpty()) {
            this.saveBtn.disabled = false;
            this.clearBtn.disabled = false;
        }
    }
}

